// Charity API connection for external social-ewallet/charities endpoint

const CHARITY_API_BASE = 'https://stg.foodservices.openapipaas.com/api/v1';
const CHARITY_API_KEY = 'Con77PLDrkoKDYmcZWSg';
const CHARITY_PROJECT_ID = '234498d1-584c-4f08-b686-452a6a7056ca';
const CHARITY_PLATFORM_SYSCODE = '100';

export const getAllCharities = async (page = 0, headers = {}) => {
  const url = `${CHARITY_API_BASE}/common/social-ewallet/charities?page=${page}`;
  return fetch(url, {
    headers: {
      'api-key': headers['api-key'] || CHARITY_API_KEY,
      'project-id': headers['project-id'] || CHARITY_PROJECT_ID,
      'datetime': headers['datetime'] || new Date().toISOString(),
      'content-sha256': headers['content-sha256'] || '',
      'Authorization': headers['Authorization'] || ''
    }
  }).then(res => res.json());
};
