import React from 'react';
import { TrendingUp, Activity } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';
import StatCard from '../components/StatCard';
import ChartCard from '../components/ChartCard';
import { mockData } from '../../../services/mockData';

const MerchantsView = () => {
  const {
    dineInTakeawayData,
    ordersOverTimeData,
    dsgdEarningMethodsData
  } = mockData;

  return (
    <div className="p-8 bg-gradient-to-br from-slate-50 via-orange-50 to-amber-50 min-h-screen">
      {/* Filters */}
      <div className="mb-8 flex gap-4">
        <select className="w-48 px-4 py-2 bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow focus:ring-2 focus:ring-orange-500 focus:border-transparent flex items-center gap-2">
          <option>Weekly</option>
          <option>Monthly</option>
          <option>Yearly</option>
        </select>
        <select className="w-64 px-4 py-2 bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow focus:ring-2 focus:ring-orange-500 focus:border-transparent">
          <option>All Locations</option>
          <option>Location 1</option>
          <option>Location 2</option>
        </select>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 gap-6 mb-8">
        <StatCard
          title="Total Transactions"
          value="2,345"
          trend="+12.5% from yesterday"
          icon={TrendingUp}
          bgGradient="bg-gradient-to-br from-red-500 to-yellow-500"
          iconColor="text-orange-400"
        />
        <StatCard
          title="Peak Transaction Hours"
          value="12pm - 1pm"
          icon={Activity}
          bgGradient="bg-gradient-to-br from-purple-500 to-blue-600"
          iconColor="text-amber-400"
        />
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-2 gap-6 mb-8">
        <ChartCard title="Dine-in vs Takeaway Trend">
          <ResponsiveContainer width="100%" height={250}>
            <LineChart data={dineInTakeawayData}>
              <XAxis dataKey="time" tick={{ fontSize: 12 }} />
              <YAxis tick={{ fontSize: 12 }} />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: 'rgba(255, 255, 255, 0.95)', 
                  border: 'none', 
                  borderRadius: '12px', 
                  boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)' 
                }} 
              />
              <Line 
                type="monotone" 
                dataKey="dineIn" 
                name="Dine-in"
                stroke="#F59E0B" 
                strokeWidth={3}
                dot={{ fill: '#F59E0B', strokeWidth: 2, r: 6 }}
                activeDot={{ r: 8, stroke: '#F59E0B', strokeWidth: 2 }}
              />
              <Line 
                type="monotone" 
                dataKey="takeaway" 
                name="Takeaway"
                stroke="#EF4444" 
                strokeWidth={3}
                dot={{ fill: '#EF4444', strokeWidth: 2, r: 6 }}
                activeDot={{ r: 8, stroke: '#EF4444', strokeWidth: 2 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </ChartCard>

        <ChartCard title="Orders Over Time">
          <ResponsiveContainer width="100%" height={250}>
            <LineChart data={ordersOverTimeData}>
              <XAxis dataKey="day" tick={{ fontSize: 12 }} />
              <YAxis tick={{ fontSize: 12 }} />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: 'rgba(255, 255, 255, 0.95)', 
                  border: 'none', 
                  borderRadius: '12px', 
                  boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)' 
                }} 
              />
              <Line 
                type="monotone" 
                dataKey="orders" 
                stroke="#EF4444" 
                strokeWidth={3}
                dot={{ fill: '#EF4444', strokeWidth: 2, r: 6 }}
                activeDot={{ r: 8, stroke: '#EF4444', strokeWidth: 2 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </ChartCard>
      </div>

      {/* DSGD Earning Methods Chart */}
      <ChartCard title="DSGD Earning Methods: Round-Up vs. Forfeit Discounts">
        <ResponsiveContainer width="100%" height={250}>
          <PieChart>
            <Pie
              data={dsgdEarningMethodsData}
              cx="50%"
              cy="50%"
              outerRadius={100}
              innerRadius={50}
              dataKey="value"
              label={({ name, value }) => `${name}: ${value}%`}
            >
              {dsgdEarningMethodsData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Pie>
            <Tooltip 
              contentStyle={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.95)', 
                border: 'none', 
                borderRadius: '12px', 
                boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)' 
              }} 
            />
          </PieChart>
        </ResponsiveContainer>
      </ChartCard>
    </div>
  );
};

export default MerchantsView;
