import React, { useState } from 'react';
import { Calendar, Filter, Search } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

// Mock data structure that can be easily replaced with API data
const mockCampaigns = [
  {
    id: 1,
    name: "Clean Water Initiative",
    organization: "WaterAid",
    category: "Environment",
    amountRaised: 45000,
    target: 100000,
    startDate: "2024-01-15",
    endDate: "2024-06-15",
    status: "Active"
  },
  {
    id: 2,
    name: "Education for All",
    organization: "UNICEF",
    category: "Education",
    amountRaised: 75000,
    target: 150000,
    startDate: "2024-02-01",
    endDate: "2024-12-31",
    status: "Active"
  },
  {
    id: 3,
    name: "Medical Supplies Drive",
    organization: "Red Cross",
    category: "Healthcare",
    amountRaised: 120000,
    target: 100000,
    startDate: "2024-01-01",
    endDate: "2024-03-31",
    status: "Completed"
  },
  {
    id: 4,
    name: "Food Security Program",
    organization: "World Food Programme",
    category: "Food Security",
    amountRaised: 85000,
    target: 200000,
    startDate: "2024-03-01",
    endDate: "2024-08-31",
    status: "Active"
  },
  {
    id: 5,
    name: "Shelter for Homeless",
    organization: "Habitat for Humanity",
    category: "Housing",
    amountRaised: 95000,
    target: 150000,
    startDate: "2024-02-15",
    endDate: "2024-07-15",
    status: "Active"
  }
];

const CampaignsView = () => {
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOption, setSortOption] = useState('progress-desc');

  // Sorting logic
  const sortedCampaigns = [...mockCampaigns].sort((a, b) => {
    const progressA = Math.min(Math.round((a.amountRaised / a.target) * 100), 100);
    const progressB = Math.min(Math.round((b.amountRaised / b.target) * 100), 100);
    switch (sortOption) {
      case 'progress-asc':
        return progressA - progressB;
      case 'progress-desc':
        return progressB - progressA;
      case 'target-asc':
        return a.target - b.target;
      case 'target-desc':
        return b.target - a.target;
      default:
        return 0;
    }
  });

  // Filter campaigns based on category and search query
  const filteredCampaigns = sortedCampaigns.filter(campaign => {
    const matchesCategory = selectedCategory === 'All' || campaign.category === selectedCategory;
    const matchesSearch = campaign.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         campaign.organization.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  // Get unique categories for filter
  const categories = ['All', ...new Set(mockCampaigns.map(campaign => campaign.category))];

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  // Calculate progress percentage
  const calculateProgress = (raised, target) => {
    return Math.min(Math.round((raised / target) * 100), 100);
  };

  const handleCampaignClick = (campaignId) => {
    navigate('/donations', { state: { selectedCampaign: campaignId } });
  };

  return (
    <div className="p-8 bg-gradient-to-br from-slate-50 via-purple-50 to-pink-50 min-h-screen">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Charity Campaigns</h1>
        <p className="text-gray-600">View and manage all active charity campaigns</p>
      </div>

      {/* Filters */}
      <div className="mb-8 flex items-center gap-4 flex-wrap">
        <div className="flex items-center gap-2">
          <Filter size={20} className="text-gray-500" />
          <span className="text-gray-700 font-medium">Category:</span>
          <select 
            className="w-48 px-4 py-2 bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        <div className="flex items-center gap-2 ml-8">
          <Search size={20} className="text-gray-500" />
          <input
            type="text"
            placeholder="Search campaigns..."
            className="w-64 px-4 py-2 bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* Sort Filter */}
        <div className="flex items-center gap-2 ml-8">
          <span className="text-gray-700 font-medium">Sort by:</span>
          <select
            className="w-40 px-4 py-2 bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            value={sortOption}
            onChange={e => setSortOption(e.target.value)}
          >
            <option value="progress-desc">Progress ↓</option>
            <option value="progress-asc">Progress ↑</option>
            <option value="target-desc">Target ↓</option>
            <option value="target-asc">Target ↑</option>
          </select>
        </div>
      </div>

      {/* Campaigns Table */}
      <div className="bg-white rounded-xl shadow-sm overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Campaign</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Organization</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Category</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Progress</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Target</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredCampaigns.map((campaign) => (
                <tr 
                  key={campaign.id} 
                  className="hover:bg-gray-50 cursor-pointer transition-colors"
                  onClick={() => handleCampaignClick(campaign.id)}
                >
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{campaign.name}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{campaign.organization}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-purple-100 text-purple-800">
                      {campaign.category}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="w-full bg-gray-200 rounded-full h-2.5 mr-2">
                        <div 
                          className="bg-purple-600 h-2.5 rounded-full" 
                          style={{ width: `${calculateProgress(campaign.amountRaised, campaign.target)}%` }}
                        ></div>
                      </div>
                      <span className="text-sm text-gray-900">{calculateProgress(campaign.amountRaised, campaign.target)}%</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{formatCurrency(campaign.amountRaised)}</div>
                    <div className="text-xs text-gray-500">of {formatCurrency(campaign.target)}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{new Date(campaign.startDate).toLocaleDateString()}</div>
                    <div className="text-xs text-gray-500">to {new Date(campaign.endDate).toLocaleDateString()}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      campaign.status === 'Active' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {campaign.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CampaignsView;
