import React, { useState, useEffect } from 'react';
import { Home, Users, Heart, Calendar } from 'lucide-react';
import HomeView from './views/HomeView';
import DonationsView from './views/DonationsView';
import MerchantsView from './views/MerchantsView';
import Sidebar from './Sidebar';
import Header from './Header';

const Dashboard = () => {
  // Initialize with HomeView (index 0)
  const [currentView, setCurrentView] = useState(0);

  // Ensure HomeView is set on initial load
  useEffect(() => {
    setCurrentView(0);
  }, []);

  const views = [HomeView, DonationsView, MerchantsView];
  const viewNames = ['Home Dashboard', 'Donations Dashboard', 'Merchants Dashboard'];
  const viewIcons = [Home, Heart, Users];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="flex">
        <Sidebar currentView={currentView} setCurrentView={setCurrentView} />
        
        <div className="flex-1">
          <Header 
            currentView={currentView} 
            viewNames={viewNames} 
            viewIcons={viewIcons} 
          />
          {React.createElement(views[currentView])}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
