const API_BASE = "http://localhost:8000/api/v1";

export const getAllDonations = () =>
  fetch(`${API_BASE}/donations/all`).then(res => res.json());

export const getAllOrders = () =>
  fetch(`${API_BASE}/orders/all`).then(res => res.json());

export const getAllUsers = () =>
  fetch(`${API_BASE}/users/all`).then(res => res.json());

// Fetch charities from the external API
export const getAllCharities = async (page = 0) => {
  const url = `/api/v1/common/social-ewallet/charities?page=${page}`;
  return fetch(url, {
    headers: {
      'api-key': 'YOUR_API_KEY',
      'project-id': 'YOUR_PROJECT_ID',
      'platform-syscode': 'YOUR_PLATFORM_SYSCODE',
      'datetime': new Date().toISOString(),
      'content-sha256': '',
      'Authorization': ''
    }
  }).then(res => res.json());
};
