import React from 'react';
import { Calendar } from 'lucide-react';

const Header = ({ currentView, viewNames, viewIcons }) => {
  const Icon = viewIcons[currentView];
  const iconColor = currentView === 0 ? 'text-emerald-600' : 
                   currentView === 1 ? 'text-pink-600' : 
                   'text-orange-600';

  return (
    <div className="bg-white border-b border-gray-200 p-6 shadow-sm">
      <div className="flex items-center gap-4">
        <Icon size={28} className={iconColor} />
        <h1 className="text-2xl font-bold text-gray-800">{viewNames[currentView]}</h1>
        <div className="ml-auto flex items-center gap-2 text-sm text-gray-500">
          <Calendar size={16} />
          <span>{new Date().toLocaleDateString()}</span>
        </div>
      </div>
    </div>
  );
};

export default Header;
