// Charity API connection for external social-ewallet/charities endpoint
import CryptoJS from 'crypto-js';
const CHARITY_API_BASE = 'https://stg.foodservices.openapipaas.com';
const CHARITY_API_KEY = 'Con77PLDrkoKDYmcZWSg';
const CHARITY_PROJECT_ID = '234498d1-584c-4f08-b686-452a6a7056ca';
const CHARITY_PLATFORM_SYSCODE = '101';
const CHARITY_SECRET_KEY = 'tIe7GAzhHPOxGxHnH41E';

// Helper to generate SHA256 hash (hex)
function sha256Hex(str) {
  return CryptoJS.SHA256(str).toString(CryptoJS.enc.Hex);
}

// Helper to generate HMAC-SHA256 signature (hex)
function hmacSha256Hex(key, str) {
  return CryptoJS.HmacSHA256(str, key).toString(CryptoJS.enc.Hex);
}

// Helper to generate headers for the charity API
export function generateCharityHeaders({
  method = 'GET',
  urlPathQuery = '',
  body = '',
  apiKey = CHARITY_API_KEY,
  projectId = CHARITY_PROJECT_ID,
  platformSyscode = CHARITY_PLATFORM_SYSCODE,
  secretKey = CHARITY_SECRET_KEY,
} = {}) {
  const isoNow = new Date().toISOString();
  const contentHash = sha256Hex(body || '');
  const signedHeaders = 'content-sha256;datetime;api-key;project-id;platform-syscode';
  // Canonical string: method + '\n' + urlPathQuery + '\n' + contentHash + '\n' + isoNow + '\n' + apiKey + '\n' + projectId + '\n' + platformSyscode
  const canonicalString = [
    method.toUpperCase(),
    urlPathQuery,
    contentHash,
    isoNow,
    apiKey,
    projectId,
    platformSyscode
  ].join('\n');
  const signature = hmacSha256Hex(secretKey, canonicalString);
  const authorization = `HMAC-SHA256 Credential=${apiKey}&SignedHeaders=${signedHeaders}&Signature=${signature}`;
  return {
    'datetime': isoNow,
    'content-sha256': contentHash,
    'Authorization': authorization,
    'api-key': apiKey,
    'project-id': projectId,
    'platform-syscode': platformSyscode,
    'Content-Type': 'application/json',
  };
}

// Generic signed request for GET or POST (Dart-style)
export const charityApiRequest = async ({ method = 'GET', endpoint, queryParams = {}, body = null }) => {
  // Build the URL with query params
  const url = new URL(`${CHARITY_API_BASE}${endpoint}`);
  Object.entries(queryParams).forEach(([key, value]) => url.searchParams.append(key, value));
  // urlPathQuery for signature: path + ?query
  const urlPathQuery = url.pathname + (url.search ? url.search : '');

  const headers = generateCharityHeaders({
    method,
    urlPathQuery,
    body: body ? JSON.stringify(body) : '', // '' for GET
  });

  return fetch(url.toString(), {
    method,
    headers,
    ...(body ? { body: JSON.stringify(body) } : {})
  }).then(res => res.json());
};

export const getAllCharities = async (page = 0) => {
  const res = await charityApiRequest({
    method: 'GET',
    endpoint: '/common/social-ewallet/charities',
    queryParams: { page: page.toString() }
  });
  // Defensive: return the array under result.data, or [] if not found
  return (res && res.result && Array.isArray(res.result.data)) ? res.result.data : [];
};
