import React from 'react';
import { Home, Users, Heart, Calendar } from 'lucide-react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const viewNames = ['Home Dashboard', 'Donations Dashboard', 'Merchants Dashboard'];
  const viewIcons = [Home, Heart, Users];
  
  // Get current view index based on path
  const getCurrentViewIndex = () => {
    const path = location.pathname;
    if (path.includes('merchants')) return 2;
    if (path.includes('donations')) return 1;
    return 0;
  };

  const currentView = getCurrentViewIndex();

  const handleViewChange = (index) => {
    switch(index) {
      case 0:
        navigate('/home');
        break;
      case 1:
        navigate('/donations');
        break;
      case 2:
        navigate('/merchants');
        break;
      default:
        navigate('/home');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="flex">
        <Sidebar currentView={currentView} setCurrentView={handleViewChange} />
        
        <div className="flex-1">
          <Header 
            currentView={currentView} 
            viewNames={viewNames} 
            viewIcons={viewIcons} 
          />
          <Outlet />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
