// Charity API connection for external social-ewallet/charities endpoint

export const getAllCharities = async (page = 0, headers = {}) => {
  const url = `/api/v1/common/social-ewallet/charities?page=${page}`;
  return fetch(url, {
    headers: {
      'api-key': headers['api-key'] || 'YOUR_API_KEY',
      'project-id': headers['project-id'] || 'YOUR_PROJECT_ID',
      'platform-syscode': headers['platform-syscode'] || 'YOUR_PLATFORM_SYSCODE',
      'datetime': headers['datetime'] || new Date().toISOString(),
      'content-sha256': headers['content-sha256'] || '',
      'Authorization': headers['Authorization'] || ''
    }
  }).then(res => res.json());
};
